var strSafe=msg.topic;
if(strSafe)
{
    strSafe=strSafe.replace(/[^a-z0-9]/gi, '_').toLowerCase();
    var loaded=context.get(strSafe)||0;
    msg.filename ="/data/node-red/charts/"+strSafe+".dat";
    //node.warn(loaded);
    if(0===loaded)
    {
        //load chart!! 
        msg.payload="load";
        context.set(strSafe,1);

        //node.warn("Loading " +strSafe);
    }
    else
    {
        //node.warn("Writing " +strSafe);
    }
    return msg;    
}
return null;

var store = global.get("sensor-"+msg.topic) || [];
store.push({
    "x": Date.now(),
    "y": msg.payload
});
global.set("sensor-" + msg.topic, store);
return msg;

var sensor_variable = msg.payload;
global.set(msg.topic + "_value", sensor_variable);
return msg;

var store = global.get("sensor-" + msg.topic) || [];
var last_datapoint = 0;
var resolution = global.get("history_resolution") || 1800;
function checkDatapoint(datapoint, index, array) {
    if (index > 0 && datapoint["x"] - array[last_datapoint]["x"] >= resolution*1000) {
        last_datapoint = index;
        return true;
    }
    return false;
}
msg.payload = [{
    "series": [msg.topic,],
    "data": [store.filter(checkDatapoint),],
    "labels": [msg.topic,]
}];
return msg;
